import React, { useContext, useState, useEffect, useRef } from "react";
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/authContext';
import logo from '../assets/Logo_Fox.png';

const Header = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);

  const quizzRef = useRef(null);
  const progressionRef = useRef(null);
  const jeuxRef = useRef(null);
  const menuRef = useRef(null);
  const ctaRef = useRef(null);

  const handleLoginLogout = () => {
    if (user) {
      logout();
    } else {
      navigate('/login');
    }
  };

  const addVibration = (e) => {
    e.target.classList.add('vibrate');
  };

  const removeVibration = (e) => {
    e.target.classList.remove('vibrate');
  };

  useEffect(() => {
    const buttons = [quizzRef.current, progressionRef.current, jeuxRef.current, menuRef.current, ctaRef.current];
    buttons.forEach(button => {
      if (button) {
        button.addEventListener('mouseover', addVibration);
        button.addEventListener('mouseout', removeVibration);
      }
    });

    return () => {
      buttons.forEach(button => {
        if (button) {
          button.removeEventListener('mouseover', addVibration);
          button.removeEventListener('mouseout', removeVibration);
        }
      });
    };
  }, []);

  return (
    <header className="header-container">
      <div className="header-title">
        <Link to="/">
          <h1>FoxAcademy</h1>
        </Link>
      </div>

      {user && (
        <nav className="top-nav">
          <button className="menu-button" onClick={() => setMenuOpen(!menuOpen)} ref={menuRef}>Menu</button>
          <div className={`dropdown-menu ${menuOpen ? 'open' : ''}`}>
            <Link to="/quizz" className="menu-buttons" ref={quizzRef}>Quizz</Link>
            <Link to="/progression" className="menu-buttons" ref={progressionRef}>Progression</Link>
            <Link to="/jeux" className="menu-buttons" ref={jeuxRef}>Jeux</Link>
          </div>
        </nav>
      )}
      <div className="header-buttons">
        <button className="btn-cta" onClick={handleLoginLogout} ref={ctaRef}>
          {user ? "Déconnexion" : "Connexion"}
        </button>
      </div>
    </header>
  );
};

export default Header;
