import React, { useEffect, useState } from 'react';
import '../styles/footer.css';

// Component Footer
const Footer = () => {
  const [isAtBottom, setIsAtBottom] = useState(false);

  useEffect(() => {
    // Check if the page is smaller than the window
    const checkHeight = () => {
      const windowHeight = window.innerHeight;
      const bodyHeight = document.body.scrollHeight;

      if (bodyHeight > windowHeight) {
        setIsAtBottom(false); // Footer in relative position
      } else {
        setIsAtBottom(true); // Footer in fixed position
      }
    };

    // Adds resize and load events
    window.addEventListener('resize', checkHeight);
    window.addEventListener('load', checkHeight);
    checkHeight();

    // Event cleanup
    return () => {
      window.removeEventListener('resize', checkHeight);
      window.removeEventListener('load', checkHeight);
    };
  }, []);

  // Rend le footer avec une classe conditionnelle
  return (
    <footer className={`footer-general ${isAtBottom ? 'fixed-footer' : ''}`}>
      <p>Â© 2024 FoxAcademy - Learn & Play. All rights reserved.</p>
    </footer>
  );
};

export default Footer;
